// Fixed Real-time Monitoring Dashboard Function
try {
    let payload = msg.payload;

    if (payload.data) {
        payload = payload.data;
    }

    let events = [];

    if (Array.isArray(payload)) {
        events = payload;
    } else if (typeof payload === 'object') {
        events = [payload];
    }

    // Transform events
    const transformedEvents = events.map((event, index) => {
        const getValue = (key, altKey) => {
            return event[key] || event[altKey] || 'N/A';
        };

        return {
            id: getValue('En No', 'en_no') || `EVT-${index}`,
            description: getValue('Event Desc', 'event_desc'),
            site: getValue('Site Name', 'site_name'),
            licensee: getValue('Licensee Name', 'licensee_name'),
            city: getValue('City Name', 'city_name'),
            state: getValue('State Cd', 'state_cd'),
            eventDate: getValue('Event Date', 'event_date'),
            notificationDate: getValue('Notification Date', 'notification_date'),
            emergencyClass: getValue('Emergency Class', 'emergency_class'),
            category: getValue('Event Category', 'event_category'),
            region: getValue('Region No', 'region_no'),
            lastUpdated: getValue('Last Updated Date', 'last_updated_date'),
            receivedAt: new Date().toISOString(),
            severity: extractSeverity(getValue('Event Text', 'event_text')),
            status: 'New'
        };
    });

    function extractSeverity(eventText) {
        if (!eventText) return 'Unknown';
        const text = eventText.toLowerCase();
        if (text.includes('medical event')) return 'High';
        if (text.includes('equipment failure')) return 'Medium';
        if (text.includes('stuck open') || text.includes('damaged')) return 'Medium';
        return 'Low';
    }

    // Calculate statistics
    const stats = {
        total: transformedEvents.length,
        byCategory: {},
        byState: {},
        bySeverity: { High: 0, Medium: 0, Low: 0, Unknown: 0 },
        recent: transformedEvents.slice(0, 10),
        criticalAlerts: transformedEvents.filter(e => e.severity === 'High')
    };

    transformedEvents.forEach(event => {
        stats.byCategory[event.category] = (stats.byCategory[event.category] || 0) + 1;
        stats.byState[event.state] = (stats.byState[event.state] || 0) + 1;
        stats.bySeverity[event.severity] = (stats.bySeverity[event.severity] || 0) + 1;
    });

    // Create main message for tables and alerts
    const mainMsg = {
        payload: {
            events: transformedEvents,
            stats: stats,
            lastUpdate: new Date().toLocaleString(),
            totalEvents: transformedEvents.length,
            criticalCount: stats.criticalAlerts.length,
            stateCounts: stats.byState
        }
    };

    // Create status message
    const statusMsg = {
        payload: `ğŸ”„ REAL-TIME: ${transformedEvents.length} events, ${stats.criticalAlerts.length} critical`
    };

    // Create chart data in EXACT format needed by ui_chart nodes
    const severityChartData = {
        series: ["Count"],
        data: [
            [stats.bySeverity.High || 0],
            [stats.bySeverity.Medium || 0], 
            [stats.bySeverity.Low || 0],
            [stats.bySeverity.Unknown || 0]
        ],
        labels: ["High", "Medium", "Low", "Unknown"]
    };

    const categoryChartData = {
        series: ["Count"],
        data: Object.entries(stats.byCategory).map(([_, count]) => [count]),
        labels: Object.keys(stats.byCategory)
    };

    const timelineChartData = {
        series: ["Events"],
        data: [[transformedEvents.length]],
        labels: [new Date().toLocaleTimeString()]
    };

    node.status({fill:"green", shape:"dot", text: `Monitoring ${transformedEvents.length} events`});

    // Return all messages
    return [mainMsg, statusMsg, {payload: severityChartData}, {payload: categoryChartData}, {payload: timelineChartData}];

} catch (error) {
    node.status({fill:"red", shape:"ring", text: "Processing error"});
    const errorMsg = { payload: `âŒ ERROR: ${error.message}` };
    return [null, errorMsg, null, null, null];
}

