    }

    let events = [];

    if (Array.isArray(payload)) {
        events = payload;
    } else if (typeof payload === 'object') {
        events = [payload];
    }

    // Transform events with timeline focus
    const transformedEvents = events.map((event, index) => {
        const getValue = (key, altKey) => {
            return event[key] || event[altKey] || 'N/A';
        };

        const eventDate = getValue('Event Date', 'event_date');
        const notificationDate = getValue('Notification Date', 'notification_date');

        return {
            id: getValue('En No', 'en_no') || `EVT-${index}`,
            description: getValue('Event Desc', 'event_desc'),
            category: getValue('Event Category', 'event_category'),
            severity: getValue('Event Text', 'event_text')?.toLowerCase().includes('medical event') ? 'High' : 'Medium',
            eventDate: eventDate,
            notificationDate: notificationDate,
            responseTime: calculateResponseTime(eventDate, notificationDate),
            month: extractMonth(eventDate),
            dayOfWeek: extractDayOfWeek(eventDate),
            hour: extractHour(eventDate)
        };
    });

    function calculateResponseTime(eventDate, notificationDate) {
        if (!eventDate || !notificationDate || eventDate === 'N/A' || notificationDate === 'N/A') return null;

        try {
            const event = new Date(eventDate);
            const notification = new Date(notificationDate);
            return Math.abs(notification - event) / (1000 * 60 * 60 * 24); // days
        } catch {
            return null;
        }
    }

    function extractMonth(dateString) {
        if (!dateString || dateString === 'N/A') return 'Unknown';
        try {
            const date = new Date(dateString);
            return date.toLocaleString('default', { month: 'long' });
        } catch {
            return 'Unknown';
        }
    }

    function extractDayOfWeek(dateString) {
        if (!dateString || dateString === 'N/A') return 'Unknown';
        try {
            const date = new Date(dateString);
            return date.toLocaleString('default', { weekday: 'long' });
        } catch {
            return 'Unknown';
        }
    }

    function extractHour(dateString) {
        if (!dateString || dateString === 'N/A') return -1;
        try {
            const date = new Date(dateString);
            return date.getHours();
        } catch {
            return -1;
        }
    }

    // Calculate timeline statistics
    const monthlyDistribution = {};
    const dailyDistribution = {};
    const hourlyDistribution = Array(24).fill(0);
    const responseTimes = [];

    transformedEvents.forEach(event => {
        // Monthly distribution
        monthlyDistribution[event.month] = (monthlyDistribution[event.month] || 0) + 1;

        // Daily distribution
        dailyDistribution[event.dayOfWeek] = (dailyDistribution[event.dayOfWeek] || 0) + 1;

        // Hourly distribution
        if (event.hour >= 0 && event.hour < 24) {
            hourlyDistribution[event.hour]++;
        }

        // Response times
        if (event.responseTime !== null) {
            responseTimes.push(event.responseTime);
        }
    });

    // Sort months chronologically
    const monthOrder = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
    const sortedMonthly = {};
    monthOrder.forEach(month => {
        if (monthlyDistribution[month]) {
            sortedMonthly[month] = monthlyDistribution[month];
        }
    });

    // Sort days chronologically
    const dayOrder = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    const sortedDaily = {};
    dayOrder.forEach(day => {
        if (dailyDistribution[day]) {
            sortedDaily[day] = dailyDistribution[day];
        }
    });

    const timelineMsg = {
        payload: {
            events: transformedEvents,
            monthlyDistribution: sortedMonthly,
            dailyDistribution: sortedDaily,
            hourlyDistribution: hourlyDistribution,
            responseTimes: responseTimes,
            avgResponseTime: responseTimes.length > 0 ? 
                responseTimes.reduce((a, b) => a + b, 0) / responseTimes.length : 0,
            totalEvents: transformedEvents.length,
            lastUpdate: new Date().toLocaleString()
        }
    };

    const statusMsg = {
        payload: `ğŸ“… TIMELINE: ${transformedEvents.length} events analyzed over time`
    };

    // Create chart data
    const monthlyChartData = {
        series: ["Events"],
        data: Object.values(sortedMonthly).map(count => [count]),
        labels: Object.keys(sortedMonthly)
    };

    const dailyChartData = {
        series: ["Events"],
        data: Object.values(sortedDaily).map(count => [count]),
        labels: Object.keys(sortedDaily)
    };

    const hourlyChartData = {
        series: ["Events"],
        data: hourlyDistribution.map(count => [count]),
        labels: Array(24).fill().map((_, i) => `${i}:00`)
    };

    const responseTimeData = {
        series: ["Days"],
        data: responseTimes.map(time => [time]),
        labels: responseTimes.map((_, i) => `Event ${i+1}`)
    };

    node.status({fill:"purple", shape:"dot", text: `Timeline data processed`});

    return [timelineMsg, statusMsg, {payload: monthlyChartData}, {payload: dailyChartData}, {payload: hourlyChartData}, {payload: responseTimeData}];

} catch (error) {
    node.status({fill:"red", shape:"ring", text: "Timeline processing error"});
    const errorMsg = { payload: `âŒ TIMELINE ERROR: ${error.message}` };
    return [null, errorMsg, null, null, null, null];
}

