    } else if (typeof payload === 'object') {
        events = [payload];
    }

    // Transform events with risk assessment focus
    const transformedEvents = events.map((event, index) => {
        const getValue = (key, altKey) => {
            return event[key] || event[altKey] || 'N/A';
        };

        const eventText = getValue('Event Text', 'event_text') || '';
        const category = getValue('Event Category', 'event_category');

        return {
            id: getValue('En No', 'en_no') || `EVT-${index}`,
            description: getValue('Event Desc', 'event_desc'),
            category: category,
            site: getValue('Site Name', 'site_name'),
            state: getValue('State Cd', 'state_cd'),
            riskScore: calculateRiskScore(category, eventText),
            impactLevel: calculateImpactLevel(category),
            probability: calculateProbability(category),
            severity: getValue('Event Text', 'event_text')?.toLowerCase().includes('medical event') ? 'High' : 'Medium',
            mitigation: suggestMitigation(category)
        };
    });

    function calculateRiskScore(category, eventText) {
        let score = 50; // Base score

        // Category-based scoring
        if (category === 'MEDICAL EVENT') score += 40;
        if (category === 'EQUIPMENT FAILURE') score += 30;
        if (category === 'DAMAGED') score += 25;
        if (category === 'STUCK OPEN') score += 20;
        if (category === 'LOST') score += 15;

        // Text-based scoring
        const text = eventText.toLowerCase();
        if (text.includes('medical event')) score += 20;
        if (text.includes('patient')) score += 15;
        if (text.includes('exposure')) score += 25;
        if (text.includes('leak')) score += 20;

        return Math.min(100, Math.max(0, score));
    }

    function calculateImpactLevel(category) {
        const impactLevels = {
            'MEDICAL EVENT': 'Critical',
            'EQUIPMENT FAILURE': 'High',
            'DAMAGED': 'Medium',
            'STUCK OPEN': 'Medium',
            'LOST': 'Low',
            'DEVIATION': 'Low'
        };
        return impactLevels[category] || 'Low';
    }

    function calculateProbability(category) {
        // Mock probability based on category
        const probabilities = {
            'MEDICAL EVENT': 'Low',
            'EQUIPMENT FAILURE': 'Medium',
            'DAMAGED': 'High',
            'STUCK OPEN': 'Medium',
            'LOST': 'High',
            'DEVIATION': 'Low'
        };
        return probabilities[category] || 'Medium';
    }

    function suggestMitigation(category) {
        const mitigations = {
            'MEDICAL EVENT': 'Enhanced training and double-check procedures',
            'EQUIPMENT FAILURE': 'Regular maintenance and backup systems',
            'DAMAGED': 'Improved handling protocols and inspections',
            'STUCK OPEN': 'Preventive maintenance and quick-response teams',
            'LOST': 'Better tracking and storage procedures',
            'DEVIATION': 'Process audits and quality controls'
        };
        return mitigations[category] || 'Review procedures and implement controls';
    }

    // Calculate risk statistics
    const riskDistribution = { Low: 0, Medium: 0, High: 0, Critical: 0 };
    const impactDistribution = { Low: 0, Medium: 0, High: 0, Critical: 0 };
    const probabilityDistribution = { Low: 0, Medium: 0, High: 0 };
    const highRiskEvents = [];

    let totalRiskScore = 0;

    transformedEvents.forEach(event => {
        // Risk distribution
        if (event.riskScore >= 80) riskDistribution.Critical++;
        else if (event.riskScore >= 60) riskDistribution.High++;
        else if (event.riskScore >= 40) riskDistribution.Medium++;
        else riskDistribution.Low++;

        // Impact distribution
        impactDistribution[event.impactLevel]++;

        // Probability distribution
        probabilityDistribution[event.probability]++;

        // High risk events
        if (event.riskScore >= 70) {
            highRiskEvents.push(event);
        }

        totalRiskScore += event.riskScore;
    });

    const avgRiskScore = transformedEvents.length > 0 ? totalRiskScore / transformedEvents.length : 0;

    const riskMsg = {
        payload: {
            events: transformedEvents,
            highRiskEvents: highRiskEvents,
            riskDistribution: riskDistribution,
            impactDistribution: impactDistribution,
            probabilityDistribution: probabilityDistribution,
            avgRiskScore: avgRiskScore,
            totalEvents: transformedEvents.length,
            highRiskCount: highRiskEvents.length,
            lastUpdate: new Date().toLocaleString()
        }
    };

    const statusMsg = {
        payload: `âš ï¸ RISK: ${transformedEvents.length} events, ${highRiskEvents.length} high risk`
    };

    // Create chart data
    const riskChartData = {
        series: ["Events"],
        data: Object.values(riskDistribution).map(count => [count]),
        labels: Object.keys(riskDistribution)
    };

    const impactChartData = {
        series: ["Events"],
        data: Object.values(impactDistribution).map(count => [count]),
        labels: Object.keys(impactDistribution)
    };

    const probabilityChartData = {
        series: ["Events"],
        data: Object.values(probabilityDistribution).map(count => [count]),
        labels: Object.keys(probabilityDistribution)
    };

    node.status({fill:"orange", shape:"dot", text: `Risk assessment completed`});

    return [riskMsg, statusMsg, {payload: riskChartData}, {payload: impactChartData}, {payload: probabilityChartData}];

} catch (error) {
    node.status({fill:"red", shape:"ring", text: "Risk assessment error"});
    const errorMsg = { payload: `âŒ RISK ERROR: ${error.message}` };
    return [null, errorMsg, null, null, null];
}
